// Format quotes
const inputData = $input.first().json;
const bookName = $('Extract Input').item.json.bookName;
const author = $('Extract Input').item.json.authorName;

let responseText = inputData.response || inputData.output || inputData.text || '❌ فشل استخراج الاقتباسات';

// Convert Markdown to HTML
responseText = responseText.replace(/\*\*(.+?)\*\*/g, '<b>$1</b>');
responseText = responseText.replace(/(?<!\*)\*(?!\*)(.+?)(?<!\*)\*(?!\*)/g, '<i>$1</i>');
responseText = responseText.replace(/__(.+?)__/g, '<b>$1</b>');

// Extract quotes as structured data
const quotes = [];
const quotePattern = /\d+[️⃣\)\.]\s*<i>"(.+?)"<\/i>[\s\S]*?📍\s*السياق:\s*(.+?)(?=\n\d+[️⃣\)\.]|\n\n|$)/g;

let match;
while ((match = quotePattern.exec(responseText)) !== null) {
  quotes.push({
    text: match[1].trim(),
    context: match[2].trim().replace(/<[^>]*>/g, '')
  });
}

// Add share button prompt
responseText += '\n\n━━━━━━━━━━━━━━━━\n📤 <i>شارك اقتباساتك المفضلة مع الأصدقاء!</i>';

return [{
  json: {
    text: responseText,
    parse_mode: 'HTML',
    bookName: bookName,
    author: author || 'مؤلف غير محدد',
    quotes: quotes,
    count: quotes.length
  }
}];
