// Format reviews summary
const inputData = $input.first().json;
const bookName = $('Extract Input').item.json.bookName;

let responseText = inputData.response || inputData.output || inputData.text || '❌ فشل تلخيص المراجعات';

// Convert Markdown to HTML
responseText = responseText.replace(/\*\*(.+?)\*\*/g, '<b>$1</b>');
responseText = responseText.replace(/(?<!\*)\*(?!\*)(.+?)(?<!\*)\*(?!\*)/g, '<i>$1</i>');
responseText = responseText.replace(/__(.+?)__/g, '<b>$1</b>');

// Extract rating
const ratingMatch = responseText.match(/[⭐]{1,5}/);
const rating = ratingMatch ? ratingMatch[0].length : 0;

// Extract positive and negative points
const positivePoints = extractBulletPoints(responseText, 'النقاط الإيجابية:', 'النقاط السلبية:');
const negativePoints = extractBulletPoints(responseText, 'النقاط السلبية:', 'الرأي السائد:');

function extractBulletPoints(text, startMarker, endMarker) {
  const startIdx = text.indexOf(startMarker);
  if (startIdx === -1) return [];

  const endIdx = text.indexOf(endMarker, startIdx);
  const section = endIdx === -1 ? text.substring(startIdx) : text.substring(startIdx, endIdx);

  const points = [];
  const bulletRegex = /•\s*(.+?)(?=•|$)/g;
  let match;

  while ((match = bulletRegex.exec(section)) !== null) {
    const point = match[1].trim().replace(/<[^>]*>/g, '').replace(/\n.*/g, '');
    if (point.length > 0) {
      points.push(point);
    }
  }

  return points;
}

// Add footer
responseText += '\n\n━━━━━━━━━━━━━━━━\n💬 <i>ملخص مبني على تحليل آراء القراء</i>';

return [{
  json: {
    text: responseText,
    parse_mode: 'HTML',
    bookName: bookName,
    rating: rating,
    positivePoints: positivePoints,
    negativePoints: negativePoints,
    summary: true
  }
}];
