// ğŸ›¡ï¸ Rate Limiter - Check User Limits
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const input = $input.first().json;
const userId = input.userId || input.sessionId;
const action = input.action || 'check';

if (!userId) {
  return [{
    json: {
      allowed: false,
      error: 'User ID is required',
      code: 'MISSING_USER_ID'
    }
  }];
}

// Rate limit configuration
const LIMITS = {
  // Per minute limits
  perMinute: 10,
  // Per hour limits  
  perHour: 100,
  // Per day limits
  perDay: 500,
  // Burst allowance
  burstLimit: 15
};

// In production, this would use Redis or a proper database
// For now, we'll simulate with in-memory storage
const now = Date.now();
const currentMinute = Math.floor(now / 60000);
const currentHour = Math.floor(now / 3600000);
const currentDay = Math.floor(now / 86400000);

// Generate mock usage data (in production, fetch from database)
const usage = generateMockUsage(userId, currentMinute, currentHour, currentDay);

// Check limits
const checks = {
  perMinute: usage.minute < LIMITS.perMinute,
  perHour: usage.hour < LIMITS.perHour,
  perDay: usage.day < LIMITS.perDay,
  burst: usage.burst < LIMITS.burstLimit
};

const allowed = checks.perMinute && checks.perHour && checks.perDay;

const response = {
  userId: userId,
  action: action,
  allowed: allowed,
  timestamp: new Date().toISOString(),

  usage: {
    minute: usage.minute,
    hour: usage.hour,
    day: usage.day,
    burst: usage.burst
  },

  limits: LIMITS,

  remaining: {
    minute: Math.max(0, LIMITS.perMinute - usage.minute),
    hour: Math.max(0, LIMITS.perHour - usage.hour),
    day: Math.max(0, LIMITS.perDay - usage.day)
  },

  resetIn: {
    minute: 60 - (Math.floor(now / 1000) % 60),
    hour: 3600 - (Math.floor(now / 1000) % 3600),
    day: 86400 - (Math.floor(now / 1000) % 86400)
  }
};

if (!allowed) {
  let limitType = 'unknown';
  let waitTime = 0;

  if (!checks.perMinute) {
    limitType = 'per_minute';
    waitTime = response.resetIn.minute;
  } else if (!checks.perHour) {
    limitType = 'per_hour';
    waitTime = response.resetIn.hour;
  } else if (!checks.perDay) {
    limitType = 'per_day';
    waitTime = response.resetIn.day;
  }

  response.error = {
    code: 'RATE_LIMIT_EXCEEDED',
    limitType: limitType,
    message: `Rate limit exceeded: ${limitType}`,
    waitTime: waitTime,
    retryAfter: new Date(now + waitTime * 1000).toISOString()
  };
}

function generateMockUsage(userId, minute, hour, day) {
  // Simulate usage based on user ID hash
  const hash = userId.toString().split('').reduce((a, b) => {
    a = ((a << 5) - a) + b.charCodeAt(0);
    return a & a;
  }, 0);

  const baseUsage = Math.abs(hash) % 5;

  return {
    minute: baseUsage,
    hour: baseUsage * 7,
    day: baseUsage * 45,
    burst: baseUsage
  };
}

console.log('ğŸ›¡ï¸ Rate limit check:', {
  userId,
  allowed,
  usage: response.usage
});

return [{ json: response }];

